def remove_image_token(input_ids, img_tok_index, loss_mask, hidden_states=None):
    mask = input_ids != img_tok_index
    filtered_input_ids = input_ids[mask].view(1, -1).to(input_ids.device)
    filtered_loss_mask = loss_mask[mask].view(1, -1).to(input_ids.device)
    if hidden_states is not None:
        filtered_hidden_states = hidden_states[:, mask[0], :]
        return filtered_input_ids, filtered_loss_mask, filtered_hidden_states
    
    return filtered_input_ids, filtered_loss_mask


def build_dataset_rank(
        tokenizer, clipmodel, clipprocessor
):
    processor = AutoProcessor.from_pretrained('llava-hf/llava-1.5-7b-hf')
    image_folder = '/data/COCO/train2017'
    
    #ds = load_dataset('json', data_files="/home/sangjun/EAGLE-LLAVA/playground/ShareGPT_V4.3_unfiltered_cleaned_split.json")
    ds = load_dataset('json', data_files="/home/sangjun/EAGLE-LLAVA/playground/llava_instruct_150k.json")
    ds = ds['train']
    ds = ds.shuffle(seed=42)
    ds1 = ds.select(range(args.start, args.end))
    original_columns1 = ds1.column_names
    num_proc = 1
    
    def contains_special_token(turn, tokenizer, special_token_id=32000):
        input_ids = tokenizer(turn).input_ids

        return special_token_id in input_ids

        
    def clip(images, coco_classes, imagenet_templates, clipmodel, clipprocessor):
        clipmodel.eval()
        device = next(clipmodel.parameters()).device

        all_texts = []
        for class_name in coco_classes:
            texts = [template.format(class_name) for template in imagenet_templates]
            all_texts.extend(texts)

        # 텍스트 토큰화 (한번만 하면 돼)
        text_inputs = clipprocessor(text=all_texts, padding=True, return_tensors="pt").to(device)
        text_features = clipmodel.get_text_features(**text_inputs)
        text_features = text_features / text_features.norm(p=2, dim=-1, keepdim=True)  # normalize

        image_inputs = clipprocessor(images=images, return_tensors="pt").to(device)
        image_features = clipmodel.get_image_features(**image_inputs)
        image_features = image_features / image_features.norm(p=2, dim=-1, keepdim=True)  # normalize

        # 유사도 측정 [batch, 텍스트 개수]
        logits = image_features @ text_features.T

        # 가장 유사한 텍스트 찾기
        max_indices = logits.argmax(dim=-1).tolist()

        best_templates = [all_texts[idx] for idx in max_indices]

        return best_templates

        
    def insert_clip_output_after_image(conversation: str, clip_output: str) -> str:
        marker = "<image>"
        if marker in conversation:
            # 첫 번째 <image> 뒤에 clip_output 추가
            return conversation.replace(marker, marker + " " + clip_output, 1)
        else:
            # <image>가 없으면 원본 문자열 그대로 반환
            return conversation
        

